CREATE MASTER KEY ENCRYPTION BY PASSWORD = 'admin@1234';

CREATE DATABASE SCOPED CREDENTIAL ankitadmin WITH IDENTITY = 'Managed Identity';

CREATE EXTERNAL FILE FORMAT ParquetFormat WITH (
    FORMAT_TYPE = PARQUET,
    DATA_COMPRESSION = 'org.apache.hadoop.io.compress.SnappyCodec'
);

CREATE EXTERNAL DATA SOURCE GoldLayer WITH (
    LOCATION = 'https://crdpadlsdev.dfs.core.windows.net/gold',
    CREDENTIAL = ankitadmin
);

CREATE EXTERNAL TABLE [dbo].[applicant_risk_profiles_external] WITH (
        LOCATION = '/applicant_risk_profiles',
        DATA_SOURCE = GoldLayer,
        FILE_FORMAT = ParquetFormat
) AS
SELECT * FROM [gold].[applicant_risk_profiles];

SELECT * FROM dbo.applicant_risk_profiles_external;
