CREATE SCHEMA silver 

CREATE VIEW silver.applicant_profiles_view
AS
SELECT
     *
FROM
    OPENROWSET(
        BULK 'https://crdpadlsdev.dfs.core.windows.net/silver/applicant_profiles',
        FORMAT = 'PARQUET'
    ) AS result1


SELECT * FROM silver.applicant_profiles_view;

SELECT DISTINCT (NAME_EDUCATION_TYPE) FROM silver.applicant_profiles_view;

SELECT DISTINCT (NAME_INCOME_TYPE) FROM silver.applicant_profiles_view;

CREATE MASTER KEY ENCRYPTION BY PASSWORD = 'admin@1234';

CREATE DATABASE SCOPED CREDENTIAL ankitadmin WITH IDENTITY = 'Managed Identity';

CREATE EXTERNAL FILE FORMAT ParquetFormat WITH (
    FORMAT_TYPE = PARQUET,
    DATA_COMPRESSION = 'org.apache.hadoop.io.compress.SnappyCodec'
);

CREATE EXTERNAL DATA SOURCE GoldLayer WITH (
    LOCATION = 'https://crdpadlsdev.dfs.core.windows.net/gold',
    CREDENTIAL = ankitadmin
);

CREATE EXTERNAL TABLE [dbo].[applicant_risk_profiles_external] WITH (
        LOCATION = '/applicant_risk_profiles',
        DATA_SOURCE = GoldLayer,
        FILE_FORMAT = ParquetFormat
) AS
SELECT * FROM [gold].[applicant_risk_profiles];

SELECT * FROM dbo.applicant_risk_profiles_external;

CREATE MASTER KEY ENCRYPTION BY PASSWORD = 'admin@1234';

CREATE DATABASE SCOPED CREDENTIAL ankitadmin WITH IDENTITY = 'Managed Identity';

CREATE EXTERNAL FILE FORMAT ParquetFormat WITH (
    FORMAT_TYPE = PARQUET,
    DATA_COMPRESSION = 'org.apache.hadoop.io.compress.SnappyCodec'
);

CREATE EXTERNAL DATA SOURCE SilverLayer WITH (
    LOCATION = 'https://crdpadlsdev.dfs.core.windows.net/silver',
    CREDENTIAL = ankitadmin
);

CREATE EXTERNAL TABLE [dbo].[applicant_profiles_view_external] WITH (
        LOCATION = '/applicant_risk_profiles_external_table',
        DATA_SOURCE = SilverLayer,
        FILE_FORMAT = ParquetFormat
) AS
SELECT * FROM [silver].[applicant_profiles_view];

SELECT * FROM dbo.applicant_profiles_view_external;
